import { api } from 'lwc';
import LightningModal from 'lightning/modal';

export default class ModalAllMulti extends LightningModal {
    @api heading = 'Second Modal, higher z-index, small size';
    @api options = [];

    handleOptionClick(e) {
        const { target } = e;
        this.close(parseInt(target.dataset.id, 10));
    }
}
