import { SchematizedData, TraceHeaders } from 'o11y/shared';
import { ActivityStopOptions } from './ActivityStopOptions';
import { Schema } from './Schema';
import { TracingHeadersOptions } from './TracingHeadersOptions';
export interface Activity {
    error(error: unknown, userSchemaOrText?: Schema | string, userData?: SchematizedData): void;
    stop(userSchemaOrText?: Schema | string, userData?: SchematizedData, stopOptions?: ActivityStopOptions): void;
    discard(): void;
    terminate(userSchemaOrText?: Schema | string, userData?: SchematizedData, stopOptions?: ActivityStopOptions): void;
    getId(): string;
    getRootId(): string;
    getIsSampled(): boolean;
    getTraceHeaders: (options: TracingHeadersOptions) => TraceHeaders;
    getStartTimestamp(): number;
    getStartPerfTime(): number;
    getStopPerfTime(): number;
}
